  let seconds = 0;
	
  onMount(() => {
    const interval = setInterval(() => {
-      $$invalidate('seconds', seconds += 1);
+      seconds += 1;
      console.log(`the component has been mounted for ${seconds} seconds`);
    }, 1000);
		
    return () => {
      clearInterval(interval);
    };
  });

